import React, { useRef } from "react";
import emailjs from "@emailjs/browser";
import { Card, Container } from "react-bootstrap";
import { EmailValidator } from "../auth";
import { response } from "express";

const ContactUs = () => {
  const form = useRef();
  const [formState, setFormState] = useState({
    email: "",
    name: "",
    message: "",
  });
  const [contactMessage, setContactMessage] = useState("");

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "contact_service",
        "contact_form",
        form.current,
        "zFlD2QMTXPcK2m3xX"
      )
      .then(
        (response) => {
          setContactMessage("Email Sent");
        },
        (error) => {
          setContactMessage("Error");
        }
      );

    function handleForm(e) {
      if (e.target.name === "email") {
        const validEmail = EmailValidator(e.target.value);
        if (!validEmail) {
          setContactMessage("Please Enter A Valid Email");
        } else {
          setContactMessage("");
        }
      } else {
        if (!e.target.value.length) {
          const name = e.target.name;
          setContactMessage(
            `${name.charAt(0).toUpperCase() + name.slice(1)} cannot be blank`
          );
        }
      }

      if (!contactMessage) {
        setFormState({ ...formState, [e.target.name]: e.target.value });
      }
    }
  };

  return (
    <Card className="contactCard" style={{ marginTop: "8rem", border: "none" }}>
      <h2 style={{ marginTop: "5rem", textAlign: "center" }}>Contact Me</h2>
      <form
        className="contactForm"
        style={{ display: "flex" }}
        ref={form}
        onSubmit={sendEmail}
      >
        <label className="nameLabel">Name:</label>
        <input className="nameInput" type="text" name="user_name" />
        <label className="emailLabel">Email:</label>
        <input className="emailInput" type="email" name="user_email" />
        <label>Message:</label>
        <textarea name="message" />
        <input className="sendBtn" type="submit" value="Send" />
      </form>
    </Card>
  );
};

export default ContactUs;
